import { createSlice, createAction } from '@reduxjs/toolkit';

const currentUser = createSlice({
  name: "currentUser",
  initialState: null,
  reducers: {
    setUser(state, action) { state = action.payload },
  }
});

export const { setUser } = currentUser.actions;
export default currentUser.reducer;
