import { createSlice, createAction } from '@reduxjs/toolkit';

const currentUser = createSlice({
  name: "currentUser",
  initialState: null,
  reducers: {
    setUser: state => state = currentUser,
  }
});

export const { setUser } = user.actions;
export default user.reducer;
