import React from 'react';
import { useDispatch, useEffect } from 'react-redux';
import { setMode } from '../reducers/codeReducer';
import { Rnd } from 'react-rnd';

import CodeBox from '../components/CodeBox';

import '../css/CodeBox.css';

const CodeBoxContainer = () => {

  const dispatch = useDispatch();

  const htmlTab = document.querySelector(".html-btn");
  const cssTab = document.querySelector(".css-btn");
  const jsTab = document.querySelector(".js-btn");

  useEffect({

  });

  return (
    <Rnd className="code-box-container">
      <button className="code-btn css-btn" onClick={() => dispatch(setMode("css"))}>CSS</button>
      <button className="code-btn html-btn" onClick={() => dispatch(setMode("html"))}>HTML</button>
      <button className="code-btn js-btn" onClick={() => dispatch(setMode("js"))}>JavaScript</button>
      <CodeBox />
    </Rnd>
  );
};

export default CodeBoxContainer;
