import React, { useState, useEffect } from 'react';

const App = () => {

  const [user, setUser] = useState({})
  const [form, setForm] = useState("")

  useEffect(() => {
    const token = localStorage.getItem("token")
    if(token){
      fetch(`http://localhost:3000/auto_login`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(resp => resp.json())
        .then(data => {
          setUser(data)
          // console.log(data)
        })
    }
  }, []);

  const handleRegister = (e) => {
    e.preventDefault();
    fetch('http://localhost:8080/users', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({
        user: {
          username: e.target[0].value,
          password: e.target[1].value,
        }
      })
    })
    .then(res => res.json())
    .then(console.log);
  };

  const handleLogin = (user) => {
    setUser(user);
  };

  const handleFormSwitch = (login) => {
    setForm(input);
  };

  const handleAuthClick = () => {
    const token = localStorage.getItem("token")
    fetch(`http://localhost:8080/user_is_authed`, {
      headers: {
        "Authorization": `Bearer ${token}`
      }
    })
      .then(resp => resp.json())
      .then(data => console.log(data));
  };

  const renderForm = () => {
    switch(form){
      case "login":
        return <LoginContainer handleLogin={handleLogin}/>
        break;
      default:
        return <RegisterForm handleLogin={handleLogin}/>
    }
  };

  return (
    <div className="App">
        <Header handleFormSwitch={handleFormSwitch}/>
        {
          renderForm()
        }
        <button onClick={handleAuthClick} className="ui button">Access Authorized Route</button>
    </div>
  );
}

export default App;
