import React from 'react';
import { useSelector } from 'react-redux';

import ComponentItem from '../components/ComponentItem';
import SearchBar from '../components/SearchBar';
import '../css/ComponentListContainer.css';

const ComponentListContainer = () => {

  const state = useSelector(state => state);

  const fetchUserComponents = () => {
    const token = localStorage.getItem("token");
    fetch("http://localhost:8080/user_components", {
      headers: {
        "Authorization": `Bearer: ${token}`
      }
    })
      .then(res => res.json())
      .then(data => {
        console.log(data.filter(c => c.user_id === state.user.currentUser.id));
      })
  };

  return (
    <div className="component-list">
      { state.user.currentUser ? fetchUserComponents() : null }
      <SearchBar />
      My Components:
      <ComponentItem />
    </div>
  );
};

export default ComponentListContainer;
