import initialState from '../state';

export const rootReducer = (state = initialState, action) => {
  switch(action.type) {
    case "loginForm":
      return {...state, loginForm: !state.loginForm}
    case "registerForm":
      return {...state, registerForm: !state.registerForm}
    default: return state
  };
};
