import { createSlice } from '@reduxjs/toolkit';
import { useDispatch } from 'react-redux';

import { resetCode } from './codeReducer';
import { resetComponents } from './loadReducer';

const currentUser = null;
const dispatch = useDispatch();

// A slice is a function from toolkit that combines the functionality
// of an action and a reducer into one
const userSlice = createSlice({
  name: "userSlice",
  initialState: {
    currentUser,
    users: [],
  },

  // actions are auto-generated by the creation of the reducers
  reducers: {
    setUsers(state, action) { state.users = action.payload },
    setUser(state, action) { state.currentUser = action.payload },
    logout(state, action) {
      state.currentUser = currentUser;
      dispatch(resetCode());
      dispatch(resetComponents());
    }
  }
});

export const { setUser, setUsers, logout } = userSlice.actions;
export default userSlice.reducer;
