import React from 'react';
import Dropzone from 'react-dropzone';
import '../css/ImportContainer.css';

const ImportContainer = () => {

  const handleImport = (file) => {
    var reader = new FileReader();
    reader.readAsText(file, "UTF-8");
    switch(file[0].type) {
      // reader.readAsText(file, "UTF-8");
      // reader.onload = function (evt) {
      //   document.getElementById("fileContents").innerHTML = evt.target.result;
      // }
      // reader.onerror = function (evt) {
      //   document.getElementById("fileContents").innerHTML = "error reading file";
      // }
      case "text/css":
        console.log("CSS uploaded!");
        console.log(file[0]);
        // dispatch(updateCSS(file[0].));
        break;
      case "text/html":
        console.log("HTML uploaded!");
        break;
      case "text/js":
        console.log("Javascript uploaded!");
        break;
      default: console.log("Something done broke...");
    }
  };

  return (
    <div className="import-container">
      <Dropzone onDrop={acceptedFile => handleImport(acceptedFile)}>
        {({getRootProps, getInputProps}) => (
            <div className="dropzone" {...getRootProps()}>
              <input {...getInputProps()} />
              <p>Drag 'n' drop files here, or click to Browse</p>
            </div>
        )}
      </Dropzone>
    </div>
  )
};

export default ImportContainer;
