import React, { useState, useEffect } from 'react';
import { configureStore, createAction } from '@reduxjs/toolkit';
import { rootReducer } from './reducers/rootReducer';
import RegisterContainer from './containers/RegisterContainer';
import LoginContainer from './containers/LoginContainer';
import NavBar from './containers/NavBar';

const App = () => {

  const store = configureStore({ reducer: rootReducer });

  const loginForm = createAction("loginForm");

  const [user, setUser] = useState({});

  useEffect(() => {
    const token = localStorage.getItem("token")
    if(token){
      fetch(`http://localhost:8080/auto_login`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(resp => resp.json())
        .then(data => {
          setUser(data)
          // console.log(data)
        })
    }
  }, []);

  const handleLogin = (user) => {
    setUser(user);
  };

  const handleFormSwitch = (input) => {
    store.dispatch(loginForm("loginForm"));
  };

  const handleAuthClick = () => {
    const token = localStorage.getItem("token")
    fetch(`http://localhost:8080/user_is_authed`, {
      headers: {
        "Authorization": `Bearer ${token}`
      }
    })
      .then(resp => resp.json())
      .then(data => console.log(data));
  };

  const renderForm = () => {
    return (
      store.getState().loginForm ? [<LoginContainer handleLogin={handleLogin}/>] : null,
      store.getState().registerForm ? [<RegisterContainer handleLogin={handleLogin}/>] : null
    );
  };

  return (
    <div className="App">
        <NavBar handleFormSwitch={handleFormSwitch}/>
        {
          renderForm()
        }
        <button onClick={handleAuthClick} className="ui button">Access Authorized Route</button>
    </div>
  );
}

export default App;
