import { createSlice } from '@reduxjs/toolkit';

const userSlice = createSlice({
  name: "userSlice",
  initialState: {
    currentUser: null,
    users: [],
  },
  reducers: {
    setUsers(state, action) { state.users = action.payload },

    setUser(state, action) {
      // state.currentUser = action.payload; // this works because it is not nested
      // console.log(state.currentUser);
      fetch("http://localhost:8080/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Accept": "application/json"
        },
        body: JSON.stringify({
          username: action.payload.username,
          password: action.payload.password
        })
      })
        .then(res => res.json())
        .then(data => {
          if (data.jwt) {
            //state.currentUser = data.user; // this fails because it is nested
            localStorage.username = data.user.username;
            localStorage.password = data.user.password;
            localStorage.token = data.jwt;
            console.log(`Welcome back ${data.user.username}!`);
          } else {
            console.log("Invalid Login");
          };

        })
    },
  }
});

export const { setUser, setUsers } = userSlice.actions;
export default userSlice.reducer;
