import { createSlice, createAction } from '@reduxjs/toolkit';

const increment = createAction('counter/increment');

const counter = createSlice({
  name: "counter",
  initialState: 0,
  reducers: {
    [increment]: state => state + 1,
    decrement: state => state - 1,
  }
});

export default counter;
